import React from 'react';
import ReactDom from 'react-dom';
let currdate=new Date();
let hrs=currdate.getHours();
let str="";
let css={
    color:'',
}
if(hrs>=0&&hrs<12){
    str='GOOD MORNING';
    css.color="blue";
}
else if(hrs>=12&&hrs<18){
    str="GOOD AFTERNOON";
    css.color="red";
}
else if(hrs>=18&&hrs<=23){
    str="GOOD NIGHT";
    css.color="yellow";
}
ReactDom
