import React from 'react';
import {useState} from 'react'
import './styles.css';

export default function Fetchdata(props) {
  const { data, hasSearched } = props;
  const {userdata,setuserdata}=useState(null);
  
  if (!hasSearched) {
    return null;
  }
  
  if (data !== "") {
    const keys = '91f58dd5ac11703a2f6f885b40b7686b8548a2d3';
    const secrets = 'c997f713d2f98f216fce98cf422029e91375e4d7';
    const handle =data;  
    
    const api = `https://codeforces.com/api/user.info?handles=${handle}&checkHistoricHandles=false&key=${keys}&secret=${secrets}`;
    async function data(element){
          let response=await fetch(api);
          if(response.ok){
            let ele=response.json();
            return ele.element;
          }
          
    }
    
    
    

    return (
      <div class="outer2">
     <div class="total_data">
      <div class="data_image">
         <img src=""></img>
      </div>
      <div class="data_table">
         <div class="grid-item .small">handle:</div>
         <div class="grid-item .large">{data("lastName")}:</div>
         <div class="grid-item .small">Organization:</div>
         <div class="grid-item .large">{data("lastName")}</div>
         <div class="grid-item .small">Location:</div>
         <div class="grid-item .large">{data("lastName")}</div>
         <div class="grid-item .small">max_rank:</div>
         <div class="grid-item .large">{data("lastName")}</div>
         <div class="grid-item .small">current_rank:</div>
         <div class="grid-item .large">{data("lastName")}</div>
         <div class="grid-item .small">current_rating:</div>
         <div class="grid-item .large">{data("lastName")}</div>
         <div class="grid-item .small">max_rating:</div>
         <div class="grid-item .large">{data("lastName")}</div>
      </div>
     </div>
     </div>
    );
  } else {
    return (
      <div className="z">
        <h3>Oops! Username not found</h3>
      </div>
    );
  }
}
