// PieChartComponent.jsx
import React from 'react';
import { Pie } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import ChartDataLabels from 'chartjs-plugin-datalabels';

ChartJS.register(ArcElement, Tooltip, Legend,ChartDataLabels);

const PieChartComponent = () => {
  const data = {
    labels: ['Red', 'Blue', 'Yellow'],
    datasets: [
      {
        label: '# of Votes',
        data: [12, 19, 3],
        backgroundColor: [
            'rgba(255, 0, 0, 1)',
            'rgba(0, 255, 0, 1)',

          'rgba(0, 0, 255, 1)',
          
        ],
        borderColor: [
          'rgba(255, 255, 255, 1)',
          'rgba(255, 255, 255, 1)',
          'rgba(255, 255, 255, 1)',
          
        ],
        borderWidth: 1,
        hoverOffset: 10,
      },
    ],
  };

  const options = {
    plugins: {
      legend: {
        position: 'top',
      },
      tooltip: {
        enabled: true,
      },
      datalabels: {
       
        // color: 'black',
        // align:'start',
        // anchor:'end',
                font: {
          weight: 'bold',
          size: 14,
        },
        // formatter: (value, context) => {
        //   return context.chart.data.labels[context.dataIndex];
        // },
      },
    },
    responsive: true,
   
  };

  return (
    <div style={{ width: '50%', margin: '0 auto' }}>
      <h2>Pie Chart with Pop-up Effect on Hover</h2>
      <div style={{
        position: 'relative',
        width: '100%',
        height: '400px',
      
        boxShadow: '0 0 20px rgba(0, 0, 0, 0.3)',
        transition: 'transform 0.3s ease-in-out, box-shadow 0.3s ease-in-out',
        overflow: 'hidden', // Ensure the pop-up effect doesn't overflow
      }}
      className="chart-container"
      >
        <Pie data={data} options={options} />
      </div>
      <style jsx>{`
        .chart-container:hover {
          transform: scale(1.1); /* Scale up the chart on hover */
          box-shadow: 0 0 30px rgba(0, 0, 0, 0.5); /* Enhance the shadow on hover */
        }
      `}</style>
    </div>
  );
};

 export default PieChartComponent;
