import React from 'react';
import ReactDom from 'react-dom';
let currdate=new Date();
let hrs=currdate.getHours();
let str="";
let css1={
    color:'red',
    textTransform:'uppercase',
}
let css2={
    color:'',
    textTransform:'uppercase',
}
if(hrs>=0&&hrs<12){
    str='GOOD MORNING';
    css2.color="blue";
}
else if(hrs>=12&&hrs<18){
    str="GOOD AFTERNOON";
    css2.color="red";
}
else if(hrs>=18&&hrs<=23){
    str="GOOD NIGHT";
    css2.color="yellow";
}
ReactDom.render(
    <h1><span style={css1}>HELLO RITIK </span><span style={css2}>{str}</span></h1>,
    document.querySelector('#root')
)
