import React from 'react';
import ReactDom from 'react-dom';
import './styles.css';
import {useState,useEffect} from 'react';
import PieChartComponent from './Piechart.jsx';
import LineChart from './Linechart.jsx';
import BarGraph from './Bargraph.jsx';

export default function Submission(props){
    const handle=props.data;

 
  
        const [accepted,setaccepted]=useState(0);
        const [tle,settle]=useState(0);
        const [wrong,setwrong]=useState(0);
        const [run,setrun]=useState(0);
        const [mpp, setMpp] = useState(new Map())
        const[mp,setmp]=useState(new Map());
        const[mrating,setmrating]=useState(new Set());
        useEffect(()=>{
            if(handle!==""){
            const api1=`https://codeforces.com/api/user.status?handle=${handle}`;
            async function fetchdata1(){
                try{
               const ele=await fetch(api1);
               if(ele.ok){
                  const ele_data=await ele.json();
                  if(ele_data){
                    ele_data.result.forEach((data)=>{
                        let programming_language=data.programmingLanguage;
                        let verdict=data.verdict;
                        setmp((prevmp)=>{
                            const updatedmp=new Map(prevmp);
                           const current_count=updatedmp.get(programming_language)||0;
                           updatedmp.set(programming_language,current_count+1);
                            return updatedmp;
                        })
                       
                        if(verdict==="OK"){
                            setaccepted((prev)=>prev+1);
                        }
                        else if(verdict==="TIME_LIMIT_EXCEEDED"){
                            settle((prev)=>prev+1);
                        }
                        else if(verdict==="RUNTIME_ERROR"){
                            setrun((prev)=>prev+1);
                        }
                        else if(verdict==="WRONG_ANSWER"){
                            setwrong((prev)=>prev+1);
                        }
                        data.problem.tags.forEach((value)=>{
                            setMpp((prevMap) => {
                                const updatedMap = new Map(prevMap);
                                const currentCount = updatedMap.get(value) || 0;
                                updatedMap.set(value, currentCount + 1);
                                return updatedMap;
                            });
                        })

                    });
                  }
               }
            }catch(err){
                console.log(err);
            }
            }
            fetchdata1();
            const api2=`https://codeforces.com/api/user.rating?handle=${handle}`;
            async function fetchdata2(){
                try{
                    let ele=await fetch(api2);
                    if(ele.ok){
                        let data=await ele.json();
                        if(data){
                            data.result.forEach((value)=>{
                                setmrating((prevrating)=>{
                                    const mrating=new Set(prevrating);
                                    mrating.add(value.oldRating);
                                    mrating.add(value.newRating);
                                    return mrating;
                                })
                            })
                        }
                    }
                }catch(err){
                    console.log(err);
                }
            }
            fetchdata2();
        }
        },[handle]);

         const programming=[];
         const programming_values=[];
         const contest=[];
         const contestRating=[];
        mp.forEach((value,index)=>{
           programming.push(index);
           programming_values.push(value);
        });

        const problem=[];
        const problem_count=[];
        mpp.forEach((value,index)=>{
             problem.push(index);
             problem_count.push(value);
        }

            )
            let c=0;
            mrating.forEach((value)=>{
                contest.push(c);
                c++;
                contestRating.push(value);
            })
     console.log(contest);
     console.log(contestRating);
        
       
        return(
            <>
            <div className="outer-pi">
           <PieChartComponent label={["TLE","AC","WA","RE"]}data={[tle,accepted,wrong,run]} text='Submissions:'></PieChartComponent>
           <PieChartComponent label={programming}data={programming_values} text='Languages used:'></PieChartComponent>
           </div>
           <div className="problem">
            <BarGraph labels={problem} data={problem_count}></BarGraph>
           </div>
           <div classNmae="rating">
            <LineChart ></LineChart>
           </div>
           </>
        )

    }
