import React from "react";

import { Line } from "react-chartjs-2";

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

function LineChart(props) {
  const data = {
    labels: props.labels,
    datasets: [
      {
        label: "Ritik",
        data:props.data,
        fill:true,
        backgroundColor: 'black',
        borderColor:'black',
      },
      {
        label: "Nikhil",
        data: [8, 3, 1, 6, 2, 9],
        fill: true,
        backgroundColor: 'black',
        borderColor:'black',
      },
    ],
  };

  const options = {
    scales: {
      y: {
        beginAtZero: true,
      },
      x: {
        beginAtZero: true,
      },
    },
    plugins: {
      legend: {
        display: true,
        position: "top",
      },
      tooltip: {
        enabled: true,
      },
    },
  };

  return <Line options={options} data={data}></Line>;
}

export default LineChart;
