import React from 'react';
import ReactDom from 'react-dom';
import './styles.css';
import {useState,useEffect} from 'react';

export default function Submission(props){
    const handle=props.data;
    const set=new Set();
    if(handle!==""){
        const [accepetd,setaccepted]=useState(null);
        const [tle,settle]=useState(null);
        const [wrong,setwrong]=useState(null);
        const [total,settotal]=useState(null);
        useEffect(()=>{
            const api=`https://codeforces.com/api/user.status?handle=${handle}`;
            async function fetchdata(){
                try{
               const ele=await fetch(api);
               if(ele.ok){
                  const ele_data=await ele.json();
                  if(ele_data){
                    ele_data.result.forEach((data)=>{
                        let programming_language=data.programmingLanguage;
                        let verdict=data.verdict;
                        mpp.add(programming_language);
                        if(verdict==="OK"){
                            setaccepted(accepted+1);
                        }
                        else if(verdict==="TIME_LIMIT_EXCEEDED"){
                            settle(tle+1);
                        }
                        else if(verdict==="RUNTIME_ERROR"){
                            setrun(run+1);
                        }
                        else if(verdict==="WRONG_ANSWER"){
                            setwrong(wrong+1);
                        }

                    });
                  }
               }
            }catch(err){
                console.log(err);
            }
            }
        },[handle]);
    }
}
