import React from 'react';
import { Bar } from 'react-chartjs-2';

const BarGraph = () => {
  const data = {
    labels: ['January', 'February', 'March', 'April', 'May', 'June'],
    datasets: [
      {
        label: 'Sales',
        data: [65, 59, 80, 81, 56, 55],
        backgroundColor: [
          'rgba(255, 99, 132, 0.5)',
          'rgba(54, 162, 235, 0.5)',
          'rgba(255, 206, 86, 0.5)',
          'rgba(75, 192, 192, 0.5)',
          'rgba(153, 102, 255, 0.5)',
          'rgba(255, 159, 64, 0.5)',
        ],
        borderColor: [
          'rgba(255, 99, 132, 1)',
          'rgba(54, 162, 235, 1)',
          'rgba(255, 206, 86, 1)',
          'rgba(75, 192, 192, 1)',
          'rgba(153, 102, 255, 1)',
          'rgba(255, 159, 64, 1)',
        ],
        borderWidth: 1,
      },
    ],
  };

  const options = {
    scales: {
      x: {
        type: 'linear', // Change the type of scale (linear, logarithmic, category)
        ticks: {
          stepSize: 1, // Adjust the step size between ticks
        },
        title: {
          display: true,
          text: 'Month', // Add a title for the x-axis
          font: {
            size: 16,
            weight: 'bold',
          },
        },
      },
      y: {
        beginAtZero: true, // Start the scale from zero
        title: {
          display: true,
          text: 'Sales', // Add a title for the y-axis
          font: {
            size: 16,
            weight: 'bold',
          },
        },
      },
    },
    plugins: {
      legend: {
        display: false,
      },
      title: {
        display: true,
        text: 'Monthly Sales',
        font: {
          size: 20,
          weight: 'bold',
        },
      },
    },
  };

  return (
    <div style={{ width: '80%', margin: '0 auto' }}>
      <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>Customized Bar Graph</h2>
      <Bar data={data} options={options} />
    </div>
  );
};

export default BarGraph;
