import React from 'react';
import { Pie } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import ChartDataLabels from 'chartjs-plugin-datalabels';

ChartJS.register(ArcElement, Tooltip, Legend, ChartDataLabels);

const PieChartComponent = (props) => {
  // Ensure props.data is available
  if (!props.data) {
    return <div>Loading...</div>; // Or any other placeholder while data is not available
  }

  const data = {
    labels:props.label,
    datasets: [
      {
        label: '# of Votes',
        data: props.data,
        backgroundColor: [
          'rgba(184, 134, 11, 1)',
          'rgba(0, 100, 0, 1)',
          'rgba(255, 140, 0, 1)',
          'rgba(139, 0, 0, 1)',
        ],
        borderColor: [
          'rgba(255, 255, 255, 1)',
          'rgba(255, 255, 255, 1)',
          'rgba(255, 255, 255, 1)',
          'rgba(255, 255, 255, 1)',
        ],
        borderWidth: 1,
        hoverOffset: 10,
      },
    ],
  };

  const options = {
   
    plugins: {
      title:{
        display:true,
        text:props.text,
     
        position:'top',
        font:{
          size:'18',
          weight:'bold',
        }
  
      },
      legend: {
        display:true,
        position: 'right',
      },
      tooltip: {
        enabled: true,
      },
      datalabels: {
        display: true,
        color: 'black',
        align: 'start',
        anchor: 'end',
        font: {
          weight: 'bold',
          size: 11,
        },
        formatter: (value, context) => {
          
          return context.chart.data.labels[context.dataIndex];
         
        },
        display: function(context) {
          return context.chart.data.datasets[0].data[context.dataIndex] > 10; // Example threshold, adjust as needed
        },
      },
    },
    responsive: true,
    maintainAspectRatio: false, // This allows the chart to take up the height of the container
  };

  return (
    <div className="pie">
      <div
        style={{
          position: 'relative',
          width: '100%',
          height: '500px', // Set a fixed height for the parent container
          boxShadow: '0 0 20px rgba(0, 0, 0, 0.3)',
          transition: 'transform 0.3s ease-in-out, box-shadow 0.3s ease-in-out',
          overflow: 'hidden',
        }}
        className="chart-container"
      >
        <Pie data={data} options={options} />
      </div>
      <style jsx>{`
        .chart-container {
          display: flex;
          justify-content: center;
          align-items: center;
        }
        .chart-container canvas {
          height: 100% !important; // Make the canvas height 100% of the container
        }
        .chart-container:hover {
          transform: scale(1.1); /* Scale up the chart on hover */
          box-shadow: 0 0 30px rgba(0, 0, 0, 0.5); /* Enhance the shadow on hover */
        }
      `}</style>
    </div>
  );
};

export default PieChartComponent;
